section.para:
Example usage: std::vector<std::vector<int>> matrix = {
    {1, 0, 1},
    {0, 1, 0},
    {1, 1, 1}
};
std::vector<double> ratios = calculateColumnRatios(matrix);
ratios: [0.666667, 0.333333, 0.666667]

section.programlisting:
00091                                                                                  {
00092     std::vector<double> ratios;
00093 
00094     if (matrix.empty() || matrix[0].empty()) {
00095         return ratios;  // Empty matrix, return empty ratios vector
00096     }
00097 
00098     const int numRows = matrix.size();
00099     const int numCols = matrix[0].size();
00100 
00101     for (int col = 0; col < numCols; col++) {
00102         int count = 0;
00103         for (int row = 0; row < numRows; row++) {
00104             if (matrix[row][col] == 1) {
00105                 count++;
00106             }
00107         }
00108         double ratio = static_cast<double>(count) / numRows;
00109         ratios.push_back(ratio);
00110     }
00111 
00112     return ratios;
00113 }

